<?php//view_include("news.tpl",['title'=>'test',...])// Подключение шаблона.// array('content'=>'содержимое сайта','title'=>'имя сайта')function view_include($fileName, $vars = array()){	// Установка переменных для шаблона.	foreach ($vars as $k => $v)	{		$$k = $v;//$title = 'test';	}	// Генерация HTML в строку.	ob_start();	include $fileName;	return ob_get_clean();	}